                return;
            case '':
                break;
            default:
                response = {
                    type: 'error',
                    content: `Command not found: ${trimmedCmd}. Type "help" for a list of commands.`,
                };
        }

        setHistory((prev) => [
            ...prev,
            { type: 'input', content: cmd },
            ...(Array.isArray(response) ? response : [response]),
        ]);
    };

